sysauto_script.script:
var rightNow = new GlideDateTime();

gs.info("From " + rightNow + " till " + gs.hoursAgo(-24) );
var needItTasks = new GlideRecord('x_58872_needit_needit_task');
//needItTasks.addQuery('due_dateBETWEENrightNow@tomorrow');
needItTasks.addQuery('due_date','>=',rightNow);
needItTasks.addQuery('due_date','<=',gs.hoursAgo(-24));
//needItTasks.addQuery('due_date','<=',gs.hoursAgo(-24));
//needItTasks.addQuery('state','!=',3);
needItTasks.query();
while(needItTasks.next()){
	gs.info("From " + rightNow + " till " + gs.hoursAgo(-24) + " NeedIt Task records Overdue in nest 24 hours = " + needItTasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',needItTasks,needItTasks.number,gs.getUserName());
}
